package java0413;

public class Ex04_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String A = "Yellow";
		
		if(A=="Red") {
			System.out.println("빨간불이 켜집니다.");
		}else if(A=="Yellow") {
			System.out.println("노란불이 켜집니다.");
		}else if(A=="Green") {
			System.out.println("초록불이 켜집니다.");
		}else {
			System.out.println("에러, 잘못된 색 입력");
		}
	}

}
